mapper.select:
select -- 레스토랑 기본 정보 pr.id, pr.name, pr.address, pr.phone, -- 레스토랑 유아 관련 정보 prf.baby_bed,prf.baby_chair,prf.baby_menu,prf.baby_tableware,prf.stroller,prf.diaper_change,prf.meeting_room,prf.nursing_room,prf.play_room,prf.parking, -- 레스토랑 이미지( 한개만 ) (select image_path from p_restaurant_images where restaurant_id = pr.id limit 1) as image_path, -- 레스토랑 리뷰 총점 ROUND((select avg(total_rating) from p_restaurant_reviews where restaurant_id= pr.id ),1) as review_total, -- 북마크 상태 (select case when count(id) > 0 then true else false end from p_restaurant_bookmarks prb where user_id = #{userId} and id = #{id} ) as isBookmarked from p_restaurants as pr left outer join p_restaurant_facilities prf on pr.id = prf.restaurant_id
prb.user_id = #{userId} and prf.baby_bed = true and prf.baby_chair = true and prf.baby_menu = true and prf.baby_tableware = true and prf.stroller = true and prf.diaper_change = true and prf.meeting_room = true and prf.nursing_room = true and prf.play_room = true and prf.parking = true
order by ST_DistanceSphere(geom, ST_MakePoint(#{lon},#{lat})) limit 10 offset #{pageNumber}
